
<script context="module">import { getCurrentComponent, getDataAttributeName, isWebComponents } from "./_utils.js";
</script>
<script>if (isWebComponents) {
  getCurrentComponent().setAttribute(getDataAttributeName(`view`), ``);
}
</script>
{ #if isWebComponents }
<slot />
{ :else }
<div { ...{ [ getDataAttributeName( `view` ) ]: `` } } { ...$$restProps }>
    <slot />
</div>
{ /if }
